import React, { Fragment } from 'react';
import { Grid, Box } from '@mui/material';
import ProductBox from './ProductBox';

function Product() {
  const items = ['test', 'test', 'test']
  const steps = [
    'Buy one of our mystery boxes', 'Get it shipped to you', 'Open your mystery box', 'Build it using the manual provided', 'Put it somewhere that looks cool'
  ]

  return (
    <Fragment>
        <Box style={{ background: 'linear-gradient(180deg, black, white)' }}>
            <h1 style={{ color: 'white', padding: 10, paddingTop: 20, marginLeft: 10 }}>Do you have what it takes</h1>

            <Grid container columnSpacing={20}>
                {items.map((item) => (
                    <Grid item xs={4}>
                        <ProductBox />
                    </Grid>
                ))}
            </Grid>

            <div style={{ marginLeft: 20, fontSize: 20 }}>
                <h1>What happens next?</h1>
                <ol>
                    {steps.map((step) => <li>{ step }</li>)}
                </ol>
            </div>

            <button style={{ marginLeft: 20, marginRight:20, width: '100%', backgroundColor: '#1c1b1c' }}>
                <p style={{ color: 'white', fontWeight: 'bold', fontSize: 30 }}>What's the worst thing you can get? Get yours now!</p>
            </button>
        </Box>


    </Fragment>
  )
}

export default Product
